mapper.update:
update myBoard
set
title = #{title},
content =
#{content}
where bno = #{bno}

mapper.select:
select bno, title, content, writer, regDate, viewCnt
from (
select bno, title, content, writer, regDate, viewCnt,
row_number() over(order by bno desc) as rNum
from myBoard
) mb
where rNum between #{rowStart} and #{rowEnd}
order by bno desc

select bno, title, content, writer, regDate, viewCnt
from (
	select bno, title, content, writer, regDate, viewCnt,
		row_number() over(order by bno desc) as rNum
	from myBoard where bno > 0
		
)mb
where rNum between #{rowStart} and #{rowEnd}
order by bno desc
